import { useDrop } from 'react-dnd';
import { ItemTypes } from './ItemTypes';

const style = {
    height: '250px',
    width: '100%',
    color: 'white',
    textAlign: 'center',
    fontSize: '1rem',
    lineHeight: 'normal',
    zIndex: 5,
    position: 'absolute',
    bottom: '150px',
    backgroundColor: 'darkkhaki'
}
export const Field = (props) => {
    console.log(props.cards)
    const [{ canDrop, isOver }, drop] = useDrop(() => ({
        accept: ItemTypes.CARD,
        drop: () => ({ name: 'Field' }),
        collect: (monitor) => ({
            isOver: monitor.isOver(),
            canDrop: monitor.canDrop(),
        }),
    }));
    const isActive = canDrop && isOver;
    if (isActive) {
        backgroundColor = 'darkgreen';
    }
    return (
        <div ref={drop} role={'Field'}>
            {isActive ? (<button>Release to drop</button>) : (<button>Drag a box here</button>)}
            <div style={{
                position: 'absolute',
                bottom: 0,
                textAlign: 'center',
                width: '100%'
            }}>
                test
            </div>
        </div>
    );
}

export default Field
