import { useState, useEffect } from "react";
import Box from "@material-ui/core/Box";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import TextField from "@material-ui/core/TextField";
import axios from 'axios'

export default function BasicSelect() {
  const [name, setName] = useState("");
  const [rarities, setRarities] = useState([]);
  const [rarity, setRarity] = useState("Common");
  const [image, setImage] = useState("")
  const [desc, setDesc] = useState("")
  const [type, setType] = useState("")
  const [atk, setAtk] = useState(0)
  const [def, setDef] = useState(0)

  const getRarities = () => {
    axios.get(`/api/cardRarity`)
      .then(res => {
        setRarities(res.data.data)
      })
  }

  useEffect(() => {
    getRarities()
  }, []);

  useEffect(() => {
    console.log("Rarity: " + rarity)
    console.log("Name: " + name)
    console.log("Image: " + image)
  }, [rarity, name, rarities, image]);

  const handleChangeName = (event) => {
    setName(event.target.value);
  };

  const handleChangeRarity = (event) => {
    setRarity(event.target.value);
  };

  const handleChangeImage = (event) => {
    setImage(event.target.value);
  };

  return (
    rarities.length > 0 && (
      <form>
        <FormControl fullWdith>
          <InputLabel>Card Rarity:</InputLabel>
          <Select
            value={rarity}
            label="Service"
            onChange={handleChangeRarity}
          >
            {rarities.map((x) => (
              <MenuItem key={x.rarityName} value={x.rarityName}>{x.rarityName}</MenuItem>
            ))}
          </Select>
        </FormControl>
        <FormControl fullWdith>
          <TextField
            id="cardName"
            name="cardName"
            label="Card Name:"

            defaultValue={name}
            onChange={handleChangeName}
            InputLabelProps={{ shrink: true }}
            variant="standard"
          />
        </FormControl>
        <FormControl fullWdith>
          <TextField
            id="cardImage"
            name="cardImage"
            label="Image Link:"
            defaultValue={image}
            onChange={handleChangeImage}
            InputLabelProps={{ shrink: true }}
            variant="standard"
          />
        </FormControl>
      </form>

    )
  )
}
