import { memo } from 'react';
import Field from './Field';
import Hand from './Hand';
import axios from "axios";
import { useEffect, useState } from "react";

const PlayerHand = memo(function Container() {

    const [onHand, setOnHand] = useState([]);
    const [onField, setOnField] = useState([]);

    const getCards = () => {
        //if cards have not loaded yet then load them from server.
        if (onHand.length === 0) {
            axios.get(`/api/card`).then((res) => {
                setOnHand(res.data.data);
            });
        }
    };

    useEffect(() => {
        getCards();
    }, []);

    useEffect(() => {
        sortHand();
    }, [onHand]);

    useEffect(() => {
        sortField()
        console.log(onField)
    }, [onField]);

    const handleCardPlayed = (index) => {     
        setOnField(prev => {
            var x = [...prev, onHand.filter(card => card.id == index - 1)[0]]
            return x
        })
        setOnHand(prev => prev.filter(card => {
            return card.id != index - 1
        }))
    }

    const playerAction = () => {
        console.log(onField)
    }

    const sortHand = () => {
        var myArray = onHand
        myArray.forEach(function (element, index) {
            element.id = index;
        });
    }

    const sortField = () => {
        var myArray = onField
        myArray.forEach(function (element, index) {
            element.id = index;
        });
        setOnField(myArray)
    }

    return (
        <div>
            <div style={{ overflow: 'hidden', clear: 'both' }}>
                <Field cards={onField} playerAction={playerAction}/>
            </div>
            <div style={{ overflow: 'hidden', clear: 'both' }}>
                <Hand cards={onHand} playCard={handleCardPlayed}/>
            </div>
        </div>
    );
});

export default PlayerHand
